var mock = require('./lib/river-mock.js');

module.exports = {
    /**
     * 普通schema进行mock
     * @param schema
     * @returns {string}
     */
    schema2mock: function(schema){
        return mock.handleMock(schema);
    },
    /**
     * if-spec规范文件进行mock，其中包含responseMap
     * @param schema
     * @return {string}
     */
    spec2mock: function(schema, q){
        var responseMap = mock.handleResponseMap(schema, q),
            ret = JSON.stringify(mock.handleMock(schema[responseMap]));

        return ret;
    }
}
